    private void jbInserirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbInserirActionPerformed
       String nome=jtNome.getText().trim();
       String prateleira=jtPrateleira.getText().trim();
       String coluna=jtColuna.getText().trim();
       String linha=jtLinha.getText().trim();
       String quantidade=jtQuantidade.getText().trim();
       
       DefaultTableModel val = (DefaultTableModel) jtb1.getModel();
            val.addRow (new String[]{nome, prateleira, coluna, linha, quantidade});
            
            jtNome.setText("");
            jtPrateleira.setText("");
            jtColuna.setText("");
            jtLinha.setText("");
            jtQuantidade.setText("");
            jtNome.requestFocus();
            
    }//GEN-LAST:event_jbInserirActionPerformed

    private void jbApagarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbApagarActionPerformed
        ((DefaultTableModel) jtb1.getModel()). removeRow(jtb1.getSelectedRow());
    }//GEN-LAST:event_jbApagarActionPerformed


